pragma solidity ^0.5.7;

contract Tasks{

    uint constant taskAssignCost = 100000000000000000;
    uint constant dayLength = 86400;
    uint private startTime;

    uint currentTask;
    uint taskCount;

    mapping (address => uint) public task;

    constructor() public{
        currentTask = 1;
        taskCount = 4;
        startTime = 0;//0 means the counter has not started, should be set after all tasks have been assigned
    }

    //Get current day (beginning from 0 at contract deployment)
    function getDay() public view returns (uint){
        return startTime==0? 0 : (now-startTime)/dayLength;
    }

    //=====
    //State modification
    //=====

    function assign() public payable returns (uint){
        require(taskCount>=currentTask);//See that there are tasks to assign
        require(msg.value==taskAssignCost);//Some cash necessary
        require(task[msg.sender] == 0);//See that no task has been assigned to this guy

        //Assign
        task[msg.sender] = currentTask;

        //Start counter by recording current time
        if (taskCount==currentTask){
            startTime = now;
        }

        //Return task id
        return currentTask++;
    }

    function done(uint taskid) public{
        require(startTime>0);//See that counter has started
        require(getDay()<=6);//Week has not ended
        require(task[msg.sender]>0);//Check if task is valid
        require(task[msg.sender]==taskid || getDay()>4);//Task is owned by sender, or 5th day has ended

        //Deassign task
        task[msg.sender] = 0;

        //Transfer reward
        msg.sender.transfer(taskAssignCost);//Money back guaranteed!
    }

}
